mapper.select:
select stuscho.id, stuscho.semester, stuscho.scholarshipNo, stuscho.reason, stuscho.instructor_id as instructorId, stuscho.status, stuscho.file, scho.`name` as scholarshipName, scho.type, scho.money, stu.studentNo, stu.nick_name as studentName, us.nick_name as instructorName
from student_scholarship stuscho, scholarship scho, student_info stu, user_info us where stuscho.studentNo=stu.studentNo and stuscho.instructor_id = us.user_id and stuscho.scholarshipNo=scho.scholarshipNo
and stuscho.semester=#{semester}
and stuscho.studentNo=#{studentNo}
and scho.type=#{type}
and stuscho.status=#{status}
and stuscho.scholarshipNo=#{scholarshipNo}

select stuscho.id, stuscho.semester, stuscho.scholarshipNo, stuscho.reason, stuscho.instructor_id, stuscho.status, stuscho.file, scho.`name` as scholarshipName, scho.type, scho.money, stu.studentNo, stu.nick_name as studentName, us.nick_name as instructorName
from student_scholarship stuscho, scholarship scho, student_info stu, user_info us where stuscho.studentNo=stu.studentNo and stuscho.instructor_id = us.user_id and stuscho.scholarshipNo=scho.scholarshipNo
and stuscho.id=#{semester}

mapper.update:
update student_scholarship
id = #{id}, semester = #{semester}, studentNo = #{studentNo}, instructor_id = #{instructorId}, status = #{status}, scholarshipNo = #{scholarshipNo}, reason = #{reason},
where id = #{id}
